import express from "express";
import upload from "../utils/uploadFiles";
import { uploadFilesController } from "../controller/uploadFilesController";
// import { membersController } from "../controller/membersController";

const router = express.Router();
const { uploadFilesPage, uploadFiles, uploadMultipleFiles } =
  uploadFilesController;

// const {
//   membersPage,
//   createNewMember,
//   deleteMember,
//   editMemberPage,
//   updateInfoMember,
// } = membersController;

const initWebRoute = (app) => {
  // router.get("/members", membersPage);
  // router.get("/edit-member/:id", editMemberPage);
  // router.post("/create-new-member", createNewMember);
  // router.post("/delete-member", deleteMember);
  // router.post("/update-info-member/:id", updateInfoMember);
  // router.get("/upload-files", uploadFilesPage);
  // router.post("/handle-upload-files", upload.single("myFile"), uploadFiles);
  // router.post(
  //   "/handle-upload-multiple-files",
  //   upload.array("myMultipleFiles", 1),
  //   uploadMultipleFiles
  // );

  return app.use("/", router);
};

export default initWebRoute;

