sourcesContent:
import pool from "../configs/connectDB";

const homeControllerPage = async (req, res) => {
  const [rows, fields] = await pool.execute("SELECT * FROM `users`");

  res.render("members", { dataUser: rows });
};

export const homeController = { homeControllerPage };

