sourcesContent:
const uploadFilesPage = async (req, res) => {
  res.render("uploadFiles");
};

const uploadFiles = async (req, res, next) => {
  const file = req.file;

  if (!file) {
    const error = new Error("Please upload a file");
    error.httpStatusCode = 400;
    return next(error);
  }

  const result = `<img src=${
    "/uploads/" + file.filename
  } width="400px" /><br/><a href="upload-files">upload more files</a>`;

  res.send(result);
};

const uploadMultipleFiles = (req, res) => {
  const files = req.files;
  let result = "";
  for (let i = 0; i < files.length; i += 1) {
    result += `<img src=${
      "uploads/" + files[i].filename
    } width="400px" height="300px" style="margin-right:20px" />`;
  }

  result += `<a href="/upload-files" > upload more files </a>`;

  res.send(result);
};

export const uploadFilesController = {
  uploadFilesPage,
  uploadFiles,
  uploadMultipleFiles,
};

